mapper.select:
select a.id,a.lsh,a.driver_code as driverCode,b.driver_name as driverName, a.path_code as pathCode,c.path_name as pathName ,a.lrsj,a.fcsj,a.status,a.sdsj, a.lrid,a.lrusername,a.truename,a.store_code as storeCode,a.dept_code as deptCode, case a.status when 0 then '新增' when 1 then '已发出' when 2 then '已送达' when 9 then '已作废' end as statusInfo,d.store_name as storeName from mf_ps a left join mf_driver b on a.driver_code = b.driver_code left join mf_path c on a.path_code = c.path_code left join mf_store d on a.store_code = d.store_code
a.lsh = #{lsh}
and a.status = #{status}
order by a.lsh desc

select billtype, lsh, hh, pinm, sl, lx, psdh, concat(ifnull(province,''),' ',ifnull(city,''),' ',ifnull(district,''),' ',ifnull(location,'')) as location, phone, contact, qwsj, sdsj,xingh,tm,ifnull(remark,'') as remark,province,city,district, store_code as storeCode,store_name as storeName,lrsj,lrid,truename, dept_code as deptCode,dept_name as deptName,driver_name as driverName,driver_phone as driverPhone, ps_status as psStatus, case ps_status when 0 then '新增' when 1 then '已发出' when 2 then '已送达' when 3 then '已作废' end as psStatusInfo, wl_status as wlStatus, case wl_status when 0 then '运输中' when 1 then '已送达' when 2 then '未送达' end as wlStatusInfo from vw_ps
driver_code = #{driverCode}
and phone = #{phone}
and lsh = #{lsh}
and hh = #{hh}
and psdh = #{psdh}
and wl_status = #{wlstatus}
and ps_status = #{psstatus}
order by lrsj desc

select a.id,a.lsh,a.driver_code as driverCode,b.driver_name as driverName, a.path_code as pathCode,c.path_name as pathName ,a.lrsj,a.fcsj,a.status,a.sdsj, a.lrid,a.lrusername,a.truename,a.store_code as storeCode,a.dept_code as deptCode, case a.status when 0 then '新增' when 1 then '已发出' when 2 then '已送达' when 9 then '已作废' end as statusInfo,e.store_name as storeName from mf_ps a left join mf_driver b on a.driver_code = b.driver_code left join mf_path c on a.path_code = c.path_code inner join mf_user d on a.store_code = d.storage_code left join mf_store e on a.store_code = e.store_code
a.lsh = #{lsh}
and a.status = #{status}
and d.id = #{uid}
order by a.lsh desc
