mapper.select:
select concat(substr(b.plbm,1,4),'00') as plbm,c.plmc as name,sum(a.kucn) as value from mf_his_kucn a left join mf_sp b on a.hh = b.hh left join mf_pl c on concat(substr(b.plbm,1,4),'00')= c.plbm where a.rq = #{rq} and a.dept_code = #{deptCode}
and a.store_code = #{storeCode}
group by concat(substr(b.plbm,1,4),'00'),c.plmc having sum(kucn)!= 0 order by 1

select b.plbm,c.plmc as name,sum(a.kucn) as value
from mf_his_kucn a
         left join mf_sp b on a.hh = b.hh
         left join mf_pl c on b.plbm = c.plbm
where a.rq = #{rq}
  and a.dept_code = #{deptCode}

    and substr(b.plbm,1,4) = substr(#{plbm},1,4)



    and a.store_code = #{storeCode}

group by b.plbm,c.plmc having sum(kucn)!= 0 order by 1

select a.lsh, case a.status when 0 then '新增' when 1 then '未派车' when 2 then '已派车' when 3 then '已送达' when 9 then '已作废' when 10 then '已撤销' end as statusInfo, hh,pinm,sl, case a.lx when 0 then '正常' when 1 then '样机' when 2 then '赠品' when 3 then '残次' end as lxInfo,a.qwsj,a.dept_code as deptCode,a.dept_name as deptName, a.xingh,a.store_code as storeCode,a.store_name as storeName, a.storage_code as storageCode,a.storage_name as storageName,a.contact,a.phone, concat(ifnull(a.province,''),' ',ifnull(a.city,''),' ',ifnull(a.district,''), ' ',ifnull(a.location,'')) as location,ckuserid,ckusername from vw_xs a where a.billtype=#{type} and lrsj between #{strq} and concat(#{torq},' 23:59:59')
and a.store_code = #{storecode}
and a.storage_code = #{storagecode}
and a.lsh = #{lsh}
and a.hh = #{hh}
and (a.plbm = #{plbm} or concat(substr(plbm,1,4),'00')=#{plbm} or concat(substr(plbm,1,2),'0000')=#{plbm})
order by a.lrsj desc

select a.dept_code as deptCode, a.store_code as storageCode, d.store_name as storageName, a.hh, b.pinm, b.xingh, c.plmc, b.plbm, sum(IF(lx = 0, kucn, 0)) as lx0, sum(IF(lx = 1, kucn, 0)) as lx1, sum(IF(lx = 2, kucn, 0)) as lx2, sum(IF(lx = 3, kucn, 0)) as lx3 from mf_kucn a left join mf_sp b on a.hh = b.hh left join mf_pl c on b.plbm = c.plbm left join mf_store d on a.store_code = d.store_code
a.dept_code = #{deptcode}
and a.store_code = #{storagecode}
and (b.plbm = #{plbm} or concat(substr(b.plbm,1,4),'00')=#{plbm} or concat(substr(b.plbm,1,2),'0000')=#{plbm})
and a.hh = #{hh}
group by a.dept_code , a.store_code , d.store_name , a.hh, b.pinm, b.xingh, c.plmc, b.plbm order by a.dept_code , a.store_code,b.plbm,a.hh

select a.dept_code as deptCode, a.store_code as storageCode, d.store_name as storageName, a.hh, b.pinm, b.xingh, c.plmc, b.plbm, sum(IF(lx = 0, kucn, 0)) as lx0, sum(IF(lx = 1, kucn, 0)) as lx1, sum(IF(lx = 2, kucn, 0)) as lx2, sum(IF(lx = 3, kucn, 0)) as lx3 from mf_kucn_rt a left join mf_sp b on a.hh = b.hh left join mf_pl c on b.plbm = c.plbm left join mf_store d on a.store_code = d.store_code
a.dept_code = #{deptcode}
and a.store_code = #{storagecode}
and (b.plbm = #{plbm} or concat(substr(b.plbm,1,4),'00')=#{plbm} or concat(substr(b.plbm,1,2),'0000')=#{plbm})
and a.hh = #{hh}
group by a.dept_code , a.store_code , d.store_name , a.hh, b.pinm, b.xingh, c.plmc, b.plbm order by a.dept_code , a.store_code,b.plbm,a.hh

select mk.dept_code as deptCode,mr.dept_name as deptName,ms.plbm,plmc, mk.store_code as storageCode,m.store_name as storageName, mk.hh,ms.pinm,ms.xingh, mk.lx,msl.lxInfo, mk.kucn as kucn,mkr.kucn as rtKucn, ifnull(dpc.sl,0) as dpcSl,ifnull(ypc.sl,0) as ypcSl from mf_kucn mk left join mf_kucn_rt mkr on mk.store_code = mkr.store_code and mk.hh = mkr.hh and mk.lx = mkr.lx left join (select hh,storage_code as store_code,lx,sum(sl) as sl from vw_xs where billtype ='XS' and status=1 group by hh,storage_code,lx) dpc on mk.store_code = dpc.store_code and mk.hh = dpc.hh and mk.lx = dpc.lx left join (select hh,storage_code as store_code,lx,sum(sl) as sl from vw_xs where billtype ='XS' and status=2 and ps_status=0 group by hh,storage_code,lx) ypc on mk.store_code = ypc.store_code and mk.hh = ypc.hh and mk.lx = ypc.lx left join mf_sp ms on mk.hh = ms.hh left join mf_sp_lx msl on mk.lx = msl.lx left join mf_store m on mk.store_code = m.store_code left join mf_region mr on mk.dept_code = mr.dept_code left join mf_pl mp on ms.plbm = mp.plbm
mk.dept_code = #{deptcode}
and mk.store_code = #{storagecode}
and (ms.plbm = #{plbm} or concat(substr(ms.plbm,1,4),'00')=#{plbm} or concat(substr(ms.plbm,1,2),'0000')=#{plbm})
and mk.hh = #{hh}
order by mk.dept_code,mk.store_code,mk.hh,mk.lx

select sj,a.ywbm,b.ywmc,a.store_code as storeCode,c.store_name as storeName,a.lsh,a.lx,a.rksl,a.cksl,
       case a.lx when 0 then '正常' when 1 then '样机' when 2 then '赠品' when 3 then '残次' end as lxInfo
from (
         select sj,ywbm,store_code,lsh,sl as rksl,null as cksl,lx from mf_kucn_in

            hh=#{hh} and dept_code = #{deptcode}


                and lx = #{lx}



                and store_code = #{storecode}



                and sj between #{strq} and concat(#{torq},' 23:59:59')


         union all
         select sj,ywbm,store_code,lsh,null as rksl,sl as cksl,lx from mf_kucn_out

            hh=#{hh}  and dept_code = #{deptcode}


                and lx = #{lx}



                and store_code = #{storecode}



                and sj between #{strq} and concat(#{torq},' 23:59:59')


     ) a
         left join mf_ywbm b on a.ywbm=b.ywbm
         left join mf_store c on a.store_code = c.store_code
order by sj
