mapper.select:
select billtype, lsh, lrid, truename, status, lrsj, hh, pinm, sl, lx,
       location, phone, contact, qwsj, sdsj,xingh,tm,
       case status
           when 0 then '新增'
           when 1 then '已提交'
           when 2 then '已派车'
           when 3 then '已送达'
           when 9 then '已作废'
           end as statusInfo,
       store_code as storeCode,store_name as storeName,
       dept_code as deptCode,dept_name as deptName
from vw_xs


        lrid = #{lrid}



        and phone = #{phone}


        and lsh = #{lsh}


        and hh = #{hh}


        and contact = #{contact}



order by lrsj desc

select billtype, lsh, lrid, a.truename, status, lrsj, hh, pinm, sl, a.lx,
location, phone, contact, qwsj, sdsj,xingh,tm,
case status
when 0 then '新增'
when 1 then '已提交'
when 2 then '已派车'
when 3 then '已送达'
when 9 then '已作废'
end as statusInfo,
a.store_code as storeCode,a.store_name as storeName,
a.dept_code as deptCode,a.dept_name as deptName
from vw_xs a


        a.store_code = #{storecode}



        and a.phone = #{phone}


        and a.lsh = #{lsh}


        and a.hh = #{hh}


        and a.contact = #{contact}


order by a.lrsj desc
